import { Component } from '@angular/core';
import { IProduct } from 'src/app/interfaces/product';
import { ProductService } from 'src/app/services/service.service';

@Component({
  selector: 'app-product-add',
  templateUrl: './product-add.component.html',
  styleUrls: ['./product-add.component.css']
})
export class ProductAddComponent {
  product= {
    name:'',
    price: 0,
    image
  };
  constructor(private productService: ProductService) {

  }
  onHandleAdd(){
    this.productService.addProduct(this.product).subscribe((data) => {
      console.log(data)
    }, error => console.log(error.message))
  }

}
<form (ngSubmit)="onHandleAdd()">
    <div class="form-group">
        <input type="text" placeholder="tên sản phẩm" [(ngModel)]="product.name" name="name">
    </div>
    <div class="form-group">
        <input type="text" placeholder="tên sản phẩm" [(ngModel)]="product.price" name="price">
    </div>
    <div *ngIf="product">{{ product.name }} - {{ product.price }}</div>
  <button type="submit">Submit</button>
</form>
